
public class TiposDeDatosPrimitivos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// existen 6 tipos de datos primitibos
		
		byte elMenorByte = Byte.MIN_VALUE;
		byte elMayorByte = Byte.MAX_VALUE;
		System.out.println("El tipo byte es un número entero de 8 bits con signo. Está comprendido entre " + elMenorByte + " y " + elMayorByte);
		
		short elMenorShort = Short.MIN_VALUE;
		short elMayorShort = Short.MAX_VALUE;
		System.out.println("El tipo short es un número entero de 16 bits con signo. Está comprendido entre " + elMenorShort + " y " + elMayorShort);
		
		
		System.out.println("El tipo int es un número entero de 32 bits con signo. Está comprendido entre " + Integer.MIN_VALUE + " y " + Integer.MAX_VALUE);
	
		System.out.println("El tipo long es un número entero de 64 bits con signo. Está comprendido entre " + Long.MIN_VALUE + " y " + Long.MAX_VALUE);
		
		System.out.println("El tipo float es un número de coma flotante de 8 bits con signo. Está comprendido entre " + Float.MIN_VALUE + " y " + Float.MAX_VALUE);
		
		System.out.println("El tipo byte es un número entero de 8 bits con signo. Está comprendido entre " + elMenorShort + " y " + elMayorShort);
		
		float variableFloat = 5.f;
		
		char a = 'a';
		
		
	}

}
